sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var ga = new GlideAjax("SendDetails");
    ga.addParam("sysparm_name", "details");
    ga.addParam("sysparm_value", newValue);
    ga.getXML(callBackFunc);

    function callBackFunc(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var result = JSON.parse(answer);
        var array = ["matter_name", "client_name","matter_type","client_type", "representation_type", "referral_source", "matter_office", "deadline", "keni_request_number", "opportunity_created", "language_requirement", "date_of_keni_request_submission", "special_matter_tags"];
        // alert(answer.matter_name);
        for (var i = 0; i < array.length; i++) {
            g_form.setValue(array[i], result[array[i]]);
        }
    }

    //Type appropriate comment here, and begin script below

}
