sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && newValue === '') {
        g_form.setSectionDisplay("immigration", false);
        return;
    }
    var matter = g_form.getValue("matter_type");
    var ga = new GlideAjax("MatterDetails");
    ga.addParam("sysparm_name", "details");
    ga.addParam("sysparm_value", matter);
    ga.getXML(callbackfunc);

    function callbackfunc(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        // alert(answer);
        if (answer == "true") {
            g_form.setSectionDisplay("immigration", true);
            makeMandatory(true);
        } else {
            clearFieldValues();
            makeMandatory(false);
            g_form.setSectionDisplay("immigration", false);

        }

    }
    var arr = ["a_number", "eoir_filling_deadline", "country_of_origin", "eoir_filling_description", "usics_description", "ice_description_ck_in", "other_deadline_description", "usics_filling_deadline", "ice_deadlines_ck_in", "other_deadlines", "date_of_last_update", "priority_date", "case_status", "action_needed", "responsible_office", "hearing_type", "hearing_location", "hearing_date"];

    function makeMandatory(val) {
        for (var i = 0; i < arr.length; i++) {
            g_form.setMandatory(arr[i], val);

        }
	}

    function clearFieldValues() {
        for (var i = 0; i < arr.length; i++) {
            g_form.clearValue(arr[i]);
        }

    }


}
