sys_script_include.script:
var ReturnSysID = Class.create();
ReturnSysID.prototype = {
    initialize: function() {},
    userDetails: function() {
        var us = [];
        var gt = new GlideRecord("x_1331380_pro_bono_time_keeper_register");
        gt.query();
        while (gt.next()) {
            us.push(gt.getValue("user"));
        }
        var rec = [];
        var gr = new GlideRecord("sys_user");
        gr.addActiveQuery();
        gr.query();
        while (gr.next()) {
            if (!us.includes(gr.getValue("sys_id"))) {
                rec.push(gr.getValue("sys_id"));
            }
        }

        return "sys_idIN" + rec;
    },

    type: 'ReturnSysID'
};
