sys_script_include.script:
var StaffingRequirement = Class.create();
StaffingRequirement.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	details:function(){
		var records=this.getParameter("sysparm_value");
		var array=records.split(",");
		var id=gs.getProperty("x_1331380_pro_bono.testing_id");
		for(var i=0;i<array.length;i++){

			var gr=new GlideRecord("x_1331380_pro_bono_m2m_x_1331380_pr_x_1331380_pr");
			gr.addQuery("x_1331380_pro_bono_staffing_requirements",array[i]);
			gr.addQuery("x_1331380_pro_bono_opportunity",id);
			gr.query();
			if(gr.next()){
				return "duplicate";
			}
			else{
				gr.initialize();
				gr.x_1331380_pro_bono_staffing_requirements=array[i];
				gr.x_1331380_pro_bono_opportunity=id;
				gr.insert();

			}
		}

	},

    type: 'StaffingRequirement'
});
