sys_script_include.script:
var StaffingOutreach = Class.create();
StaffingOutreach.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    details: function() {
        var records = this.getParameter("sysparm_value");//getting the sleceted sysids
        var array = records.split(",");
        var opportunity_id = this.getParameter("opportunity_id");
        for (var i = 0; i < array.length; i++) {
            var gt = new GlideRecord("x_1331380_pro_bono_time_keeper_register");
            gt.get(array[i]);
            var gr = new GlideRecord("sn_customerservice_related_party");
             gr.addQuery("x_1331380_pro_bono_opportunity", opportunity_id);
			// gr.addQuery("case_record", opportunity_id);
            gr.addQuery("x_1331380_pro_bono_time_keeper", array[i]);
            gr.query();
            if (!gr.next()) {
                gr.initialize();
				gr.x_1331380_pro_bono_opportunity=opportunity_id;
				gr.x_1331380_pro_bono_time_keeper=array[i];
				gr.x_1331380_pro_bono_timekeeper_name = gt.user.name;
                gr.x_1331380_pro_bono_timekeeper_email = gt.user.email;
				gr.x_1331380_pro_bono_employee_number=gt.user.employee_number;
				gr._1331380_pro_bono_practice_group=gt.practice_group;
				gr.x_1331380_pro_bono_sub_parctice_group=gt.subpractice_group;
				// gr.x_1331380_pro_bono_time_keeper_type=gt.time_keeper_type;
				// gr.x_1331380_pro_bono_opportunity_accepted=gt.accepted_opportunity;
				gr.x_1331380_pro_bono_notes=gt.notes.getJournalEntry(-1);
                gr.insert();
            }
        }
    },
    type: 'StaffingOutreach'
});
