sys_script_include.script:
var SendDetails = Class.create();
SendDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	details:function(){
		var gr=new GlideRecord("x_1331380_pro_bono_opportunity");
		var sysID=this.getParameter("sysparm_value");
		gr.addQuery("sys_id",sysID);
		gr.query();
		if(gr.next()){
			var obj={};
			obj.matter_name = gr.getValue("matter_name");
			obj.client_name=gr.getValue("client_name");
			obj.representation_type=gr.getDisplayValue("representation_type");
			obj.matter_type=gr.getDisplayValue("matter_type");
			obj.client_type=gr.getDisplayValue("client_type");
			obj.referral_source=gr.getDisplayValue("referral_source");
			obj.matter_office=gr.getValue("matter_office");
			obj.deadline=gr.getDisplayValue("deadline");
			obj.keni_request_number=gr.getValue("keni_request_number");
			obj.opportunity_created=gr.getDisplayValue("opportunity_created");
			obj.language_requriement=gr.getDisplayValue("language_requriement");
			obj.date_of_keni_request_submission=gr.getDisplayValue("date_of_keni_request_submission");
			obj.special_matter_tags=gr.getDisplayValue("special_matter_tags");
			return JSON.stringify(obj);

		}




	},

    type: 'SendDetails'
});
