sys_script_include.script:
var MatterDetails = Class.create();
MatterDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	details:function(){
		var sysID=this.getParameter("sysparm_value").split(",");
		for(var i=0;i<sysID.length;i++){
			var gr=new GlideRecord("x_1331380_pro_bono_foundation_attribute_data");
			gr.get(sysID[i].toString());
			var value=gr.getValue("value");
			var imm=value.toLowerCase();
			if(imm.includes("immigration")){
				return 'true';
			}


		}
		return "false";


	},

    type: 'MatterDetails'
});
